sections.code:
// Basis Schadenberechnung
finalDamage = weaponBaseDamage * upgradeMultiplier * elementalBonus

// Upgrade Multiplier
upgradeMultiplier = 1.0 + (upgradeLevel * 0.1) + branchBonuses

// Branch Bonuses (additiv)
powerBranch = upgradeLevel * 0.075  // +7.5% pro Level
precisionBranch = (critChance * critDamage) / 100
elementalBranch = elementalDamage + statusEffectDamage

// Kritischer Treffer
if (isCritical) {
    finalDamage *= (1.0 + critDamageBonus)
}

// Elementar DoT
if (hasElementalEffect) {
    applyDamageOverTime(finalDamage * 0.3, duration: 5s)
}
